const fs = require('fs');
const path = require('path');

function isLernaProject() {
  let root = process.cwd();

  while (!root.match(/^(\w:\\|\/)$/)) {
    root = path.resolve(root, '..');
    const file = path.join(root, 'lerna.json');
    if (fs.existsSync(file)) {
      return root;
    }
  }

  return false;
}

module.exports = isLernaProject;
