function validateLayouts(layouts, { defaultLayout, staticLayout }) {
  if (
    !layouts[defaultLayout] &&
    defaultLayout !== 'default-layout' &&
    defaultLayout !== 'none'
  ) {
    throw new Error(
      `Configuration error: layout with the name ${defaultLayout} does not exist`
    );
  }
  if (!layouts[staticLayout] && staticLayout !== 'static-layout') {
    throw new Error(
      `Configuration error: layout with the name ${defaultLayout} does not exist`
    );
  }
}

module.exports = validateLayouts;
