const cachedPackageJson = require('./cached-package-json');

function isDevDependency(packageName, customPackageJson) {
  const packageJson = customPackageJson || cachedPackageJson();

  return (
    packageJson.devDependencies &&
    Object.keys(packageJson.devDependencies).indexOf(packageName)
  );
}

module.exports = isDevDependency;
