#!/usr/bin/env node
const path = require('path');
const fs = require('fs');

function init() {
  const projectDir = process.env.INIT_CWD || path.resolve('../../', __dirname);
  const packageJsonLocation = path.join(projectDir, 'package.json');

  if (fs.existsSync(packageJsonLocation)) {
    const packageJson = JSON.parse(fs.readFileSync(packageJsonLocation));

    if (packageJson.scripts && packageJson.scripts.start) {
      return;
    }

    packageJson.scripts = packageJson.scripts || {};
    packageJson.scripts.start = 'fesk-setup';

    fs.writeFileSync(packageJsonLocation, JSON.stringify(packageJson, null, 2));
  }
}

init();
