const watch = require('../lib/watch');
const filter = require('metalsmith-filter');

function indexPage(metalsmith, config) {
  return metalsmith
    .source(`./src`)
    .destination('./dist')
    .use(filter('index.html'))
    .use(watch(config));
}

module.exports = {
  name: 'index-page',
  metalsmith: indexPage,
  path: 'index.html',
};
